interface JobCard {
    avatar: string; // URL của hình ảnh đại diện cho công việc hoặc công ty
    companyName: string; // Tên của công ty
    location: string; // Địa điểm làm việc
    jobName: string; // Tên công việc
    jobDescription: string; // Mô tả ngắn gọn về công việc
    time: string; // Thời gian đăng tin hoặc thời gian làm việc (ví dụ: "2 ngày trước" hoặc "Toàn thời gian")
  }
  